        //         <input type="text" placeholder="Usu치rio" value={username} onChange={(e) => setUsername(e.target.value)} />
        //         <input type="password" placeholder="Senha" value={password} onChange={(e) => setPassword(e.target.value)} />
        //         <button type="submit">Entrar</button>
        //     </form>
        //     <p>{message}</p>
        // </div>
        <div style={{display: "flex", flexDirection: "row", justifyContent: "center", alignItems: "center", height:"100vh"}}>
            <Card style={{width: "350px", display: "flex", flexDirection: "row", justifyContent: "center", alignItems: "center",}}>
            <Form style={{width: "220px", padding: "10px"}}>
                <Input />
                <Input />
                <Button style={{width: "220px", borderRadius: "12px"}} />
            </Form>
            </Card>
        </div>
    );
};

export default Login;
